var mout = require('mout');

/**
 * Description
 * @method createError
 * @param {} msg
 * @param {} code
 * @param {} props
 * @return err
 */
function createError(msg, code, props) {
    var err = new Error(msg);
    err.code = code;

    if (props) {
        mout.object.mixIn(err, props);
    }

    return err;
}

module.exports = createError;
