var kindOf = require('./kindOf');
    /**
     * Check if value is from a specific "kind".
     */
    /**
     * Description
     * @method isKind
     * @param {} val
     * @param {} kind
     * @return BinaryExpression
     */
    function isKind(val, kind){
        return kindOf(val) === kind;
    }
    module.exports = isKind;

