
    /**
    * Count number of full steps.
    */
    /**
     * Description
     * @method countSteps
     * @param {} val
     * @param {} step
     * @param {} overflow
     * @return val
     */
    function countSteps(val, step, overflow){
        val = Math.floor(val / step);

        if (overflow) {
            return val % overflow;
        }

        return val;
    }

    module.exports = countSteps;

