
    /**
    * Floor value to full steps.
    */
    /**
     * Description
     * @method floor
     * @param {} val
     * @param {} step
     * @return BinaryExpression
     */
    function floor(val, step){
        step = Math.abs(step || 1);
        return Math.floor(val / step) * step;
    }
    module.exports = floor;

