
    /**
    * Loops value inside range.
    */
    /**
     * Description
     * @method loop
     * @param {} val
     * @param {} min
     * @param {} max
     * @return ConditionalExpression
     */
    function loop(val, min, max){
        return val < min? max : (val > max? min : val);
    }

    module.exports = loop;

