
    /**
    * Gets normalized ratio of value inside range.
    */
    /**
     * Description
     * @method norm
     * @param {} val
     * @param {} min
     * @param {} max
     * @return BinaryExpression
     */
    function norm(val, min, max){
        return (val - min) / (max - min);
    }
    module.exports = norm;

