
    /**
     * Round number to a specific radix
     */
    /**
     * Description
     * @method round
     * @param {} value
     * @param {} radix
     * @return BinaryExpression
     */
    function round(value, radix){
        radix = radix || 1; // default round 1
        return Math.round(value / radix) * radix;
    }

    module.exports = round;


