var map = require('./map');
var prop = require('../function/prop');

    /**
     * Extract a list of property values.
     */
    /**
     * Description
     * @method pluck
     * @param {} obj
     * @param {} propName
     * @return CallExpression
     */
    function pluck(obj, propName){
        return map(obj, prop(propName));
    }

    module.exports = pluck;


