define(function(){
    /**
    * Checks if value is inside the range.
    */
    /**
     * Description
     * @method inRange
     * @param {} val
     * @param {} min
     * @param {} max
     * @param {} threshold
     * @return LogicalExpression
     */
    function inRange(val, min, max, threshold){
        threshold = threshold || 0;
        return (val + threshold >= min && val - threshold <= max);
    }

    return inRange;
});
