define(function(){
    /**
    * Linear interpolation.
    * IMPORTANT:will return `Infinity` if numbers overflow Number.MAX_VALUE
    */
    /**
     * Description
     * @method lerp
     * @param {} ratio
     * @param {} start
     * @param {} end
     * @return BinaryExpression
     */
    function lerp(ratio, start, end){
        return start + (end - start) * ratio;
    }

    return lerp;
});
