define(function () {

    /**
     * Get sign of the value.
     */
    /**
     * Description
     * @method sign
     * @param {} val
     * @return ConditionalExpression
     */
    function sign(val) {
        var num = Number(val);
        if (num === 0) return num; // +0 and +0 === 0
        if (isNaN(num)) return num; // NaN
        return num < 0? -1 : 1;
    }

    return sign;

});
