define(['../lang/toString'], function(toString) {

    var ESCAPE_CHARS = /[\\.+*?\^$\[\](){}\/'#]/g;

    /**
     * Escape RegExp string chars.
     */
    /**
     * Description
     * @method escapeRegExp
     * @param {} str
     * @return CallExpression
     */
    function escapeRegExp(str) {
        str = toString(str);
        return str.replace(ESCAPE_CHARS,'\\$&');
    }

    return escapeRegExp;

});
