var toString = require('../lang/toString');
    /**
     * Remove non-word chars.
     */
    /**
     * Description
     * @method removeNonWord
     * @param {} str
     * @return CallExpression
     */
    function removeNonWord(str){
        str = toString(str);
        return str.replace(/[^0-9a-zA-Z\xC0-\xFF \-_]/g, '');
    }

    module.exports = removeNonWord;

