var toString = require('../lang/toString');
    /**
     * Remove HTML tags from string.
     */
    /**
     * Description
     * @method stripHtmlTags
     * @param {} str
     * @return CallExpression
     */
    function stripHtmlTags(str){
        str = toString(str);

        return str.replace(/<[^>]*>/g, '');
    }
    module.exports = stripHtmlTags;

