var lpad = require('../string/lpad');

    /**
     * Add padding zeros if n.length < minLength.
     */
    /**
     * Description
     * @method pad
     * @param {} n
     * @param {} minLength
     * @param {} char
     * @return CallExpression
     */
    function pad(n, minLength, char){
        return lpad(''+ n, minLength, char || '0');
    }

    module.exports = pad;


