
    /**
     * Bitwise circular shift right
     * http://en.wikipedia.org/wiki/Circular_shift
     */
    /**
     * Description
     * @method ror
     * @param {} val
     * @param {} shift
     * @return BinaryExpression
     */
    function ror(val, shift){
        return (val >> shift) | (val << (32 - shift));
    }
    module.exports = ror;

