var arrMax = require('../array/max');
var values = require('./values');

    /**
     * Returns maximum value inside object.
     */
    /**
     * Description
     * @method max
     * @param {} obj
     * @param {} compareFn
     * @return CallExpression
     */
    function max(obj, compareFn) {
        return arrMax(values(obj), compareFn);
    }

    module.exports = max;

