define(['./map'], function (map) {

    /**
     * Extract a list of property values.
     */
    /**
     * Description
     * @method pluck
     * @param {} list
     * @param {} key
     * @return CallExpression
     */
    function pluck(list, key) {
        return map(list, function(value) {
            return value[key];
        });
    }

    return pluck;

});
