define(['../time/now'], function (now) {

    /**
     */
    /**
     * Description
     * @method throttle
     * @param {} fn
     * @param {} delay
     * @return throttled
     */
    function throttle(fn, delay){
        var context, timeout, result, args,
            cur, diff, prev = 0;
        /**
         * Description
         * @method delayed
         * @return 
         */
        function delayed(){
            prev = now();
            timeout = null;
            result = fn.apply(context, args);
        }
        /**
         * Description
         * @method throttled
         * @return result
         */
        function throttled(){
            context = this;
            args = arguments;
            cur = now();
            diff = delay - (cur - prev);
            if (diff <= 0) {
                clearTimeout(timeout);
                prev = cur;
                result = fn.apply(context, args);
            } else if (! timeout) {
                timeout = setTimeout(delayed, diff);
            }
            return result;
        }
        return throttled;
    }

    return throttle;

});
