define(function(){
    /**
    * Check if value is close to target.
    */
    /**
     * Description
     * @method isNear
     * @param {} val
     * @param {} target
     * @param {} threshold
     * @return BinaryExpression
     */
    function isNear(val, target, threshold){
        return (Math.abs(val - target) <= threshold);
    }
    return isNear;
});
