define(['../lang/toString', './truncate'], function (toString, truncate) {
    /**
     * Truncate string at full words.
     */
     /**
      * Description
      * @method crop
      * @param {} str
      * @param {} maxChars
      * @param {} append
      * @return CallExpression
      */
     function crop(str, maxChars, append) {
         str = toString(str);
         return truncate(str, maxChars, append, true);
     }

     return crop;
});
